import React, { Component } from 'react';
import './Rates.css';
import axios from 'axios';
import Currency from './Currency'


class Rates extends Component {

        constructor(props) {
            super(props);
            this.state = {
                data: {
                        id: "dollar",
                        base: "USD",
                        date: "",
                        rates: [],
                }
            };
        }

        fetchCurrencyData() {
        axios('https://api.exchangerate-api.com/v4/latest/USD')
        .then((response) => { 
                var result = response.data;
                var res = [];
                res.push(result.rates);
                result.rates = res;
                this.setState({data: result});
            })
            .catch(err => console.log(err));
        }

        componentDidMount() {

        this.fetchCurrencyData();
        //this.interval = setInterval(() => this.fetchCurrencyData(), 60 * 1000);
        }

        render() {
          //  console.log(this.state.data);
           console.log(this.state.data);
            var rates = this.state.data.map((currency) =>
                <Currency data={currency} key={currency.id} />
                );
            return (
                <div className="rates-container">
                    <ul className="rates">{rates}</ul>
                    <p></p>
               </div>
            );
        }
    }

    export default Rates;
