import React, { Component } from 'react';
import axios from 'axios';
import './Days.css';
import Day from './Day';

class Days extends Component {
  constructor(props) {
            super(props);
            this.state = {
                data: [{
                        date: "Date",
                        symbol: "Symbol",
                        temperature: '',
                },
                  {
                        date: "23.05.2019",
                        symbol: "Clouds",
                        temperature: '25C',
                },
                {
                        date: "24.05.2019",
                        symbol: "Wind",
                        temperature: '35C',
                },
                {
                        date: "25.05.2019",
                        symbol: "Rain",
                        temperature: '45C',
                },
                {
                        date: "26.05.2019",
                        symbol: "Snow",
                        temperature: '55C',
                },
                ]
            };
        }

   fetchWeather() {
        var results = [];
        var icon_url = 'http://openweathermap.org/img/w/';
        var lat = 50.433334;
        var lon = 30.516666;
        var url = "http://api.openweathermap.org/data/2.5/forecast?lat="+ lat + "&lon=" + lon + "&APPID=47246b58dee8b9eba96fa2308e7d54e2";
        axios(url)
        .then((response) => { 
                var result = response.data;
                // get 3 values from result
                results = result.list.map(function(day){
                        return {date: formatDate(day.dt_txt),
                                symbol: icon_url + day.weather[0].icon + '.png',
                                temperature: formatTemp(day.main.temp),
                                }
                    });

                function formatDate(date){
                        return date.slice(8, 10) + '.' + date.slice(5, 7)
                }

                function formatTemp(temp){
                    return (temp - 273.15).toFixed(1) + ' C';
                }

                // write received values to state
                var five_results = []
                for (var i = 0; i < results.length; i += 8){
                    five_results.push(results[i]);
                }
                this.setState({data: five_results})
                /*var result_rates = []; 
                result_rates.push(result.rates.GBP);   // replace received data with necessary currency
                result_rates.push(result.rates.EUR);
                result_rates.push(result.rates.USD);
                result.rates = result_rates;
                result_rates = [];
                result.time_last_updated = String(result.time_last_updated);   // make time look better
                result.time_last_updated = result.time_last_updated.slice(0,2) + ':' + result.time_last_updated.slice(2,4) + ':' + result.time_last_updated.slice(4,6);
                result_array.push(result);    // make array out of object to use 'map' later
                var data_arrays = this.state.data;  // clone current state
                for (var j = 0; j < result_array.length; j++){
                data_arrays[j] = result_array[j];   // update cloned state
                }
                this.setState({data: data_arrays});  // update original state
                */

            })
            .catch(err => console.log(err));
    };

    componentDidMount() {

        this.fetchWeather();
        this.interval = setInterval(() => this.fetchWeather(), 60 * 10000);
  }

   render() {
   var days = this.state.data.map((day) =>
        <Day data={day} key={day.date} />
            );

  return (
    <div className="Weather-container">
      <ul className="Weather-days">{days}
      </ul>
      </div>
  );
}
}

export default Days;
