import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import axios from 'axios';
import './Days.css';
import Day from './Day';

class Days extends Component {
  constructor(props) {
            super(props);
            this.state = {
                data: [{
                        date: "Day_1",
                        symbol: "Symbol",
                        temperature: 'Temp',
                },
                  {
                        date: "Day_2",
                        symbol: "Symbol",
                        temperature: 'Temp',
                },
                {
                        date: "Day_3",
                        symbol: "Symbol",
                        temperature: 'Temp',
                },
                {
                        date: "Day_4",
                        symbol: "Symbol",
                        temperature: 'Temp',
                },
                {
                        date: "Day_5",
                        symbol: "Symbol",
                        temperature: 'Temp',
                },
                ],
                isDetailed: false, // to show detailed information
                fullWeather: '',  // save time and all data at once
            };
        }

   fetchWeather() {
        var results = [];
        var lat = 50.433334;
        var lon = 30.516666;
        var url = "http://api.openweathermap.org/data/2.5/forecast?lat="+ lat + "&lon=" + lon + "&APPID=47246b58dee8b9eba96fa2308e7d54e2";
        axios(url)
        .then((response) => { 
                var result = response.data;
                // get 3 values from result
                results = result.list.map(function(day){
                        return {date: formatDate(day.dt_txt),
                                symbol: formatIcon(day.weather[0].icon),
                                temperature: formatTemp(day.main.temp),
                                }
                    });
                this.setState({fullWeather: results})  // save detailed results
                function formatDate(date){
                        // return month and day
                        return date.slice(8, 10) + '.' + date.slice(5, 7)
                }

                function formatIcon(icon){
                     // return icon image
                    var icon_url = 'http://openweathermap.org/img/w/';
                    return icon_url + icon + '.png'
                }

                function formatTemp(temp){
                     // return celcium results
                    return (temp - 273.15).toFixed(1) + ' C';
                }

                // write received values to state
                var five_results = []
                for (var i = 0; i < results.length; i += 8){
                    five_results.push(results[i]);
                }
                this.setState({data: five_results})
            })
            .catch(err => console.log(err));
    };

    componentDidMount() {

        this.fetchWeather();
        this.interval = setInterval(() => this.fetchWeather(), 60 * 10000);
    };

    onDayClick(date) {
      console.log(date);
      console.log(this.state.fullWeather);
    };

   render() {
   var days = this.state.data.map((day) => 
        <Day data={day} key={day.date} onClick={() => this.onDayClick(day.date)}/>
            );

  return (
    <div className="Weather-container">
      <ul className="Weather-days">{days}
      </ul>
       <ul className="Weather-details">{this.state.isDetailed ? days : false}
      </ul>
      </div>
  );
}
}

export default Days;
